odoo.define("stock_barcodes.FormRenderer", function(require) {
    "use strict";

    var core = require("web.core");
    var FormRenderer = require("web.FormRenderer");
    const WebClientObj = require("web.web_client");
    var BrowserDetection = require('web.BrowserDetection');

    FormRenderer.include({
        _barcodeModels: [
            "wiz.stock.barcodes.read",
            "wiz.stock.barcodes.read.picking",
            "wiz.stock.barcodes.read.inventory",
            "stock.picking.type",
            "stock.picking",
            "wiz.stock.barcodes.read.todo",
            "stock.barcodes.action",
        ],
        _is_allowedModel: function(){
            return this._barcodeModels.indexOf(this.modelName) !== -1
        },
        init: function() {
            this._super.apply(this, arguments);
            if (this._is_allowedModel()) {
                this.kanban_action_button_selected = 0;
                this.BrowserDetection = new BrowserDetection();
            }
        },
        start: function() {
            if (this._is_allowedModel()) {
                if (this.call("bus_service", "isMasterTab")) {
                    this.call(
                        "bus_service",
                        "addChannel",
                        "stock_barcodes_read-" + this.initialState.data.id
                    );
                    this.call(
                        "bus_service",
                        "addChannel",
                        "stock_barcodes_sound-" + this.initialState.data.id
                    );
                }
                this.$sound_ok = $(
                    '<audio src="/stock_barcodes/static/src/sounds/bell.wav" preload="auto"></audio>'
                );
                this.$sound_ko = $(
                    '<audio src="/stock_barcodes/static/src/sounds/error.wav" preload="auto"/>'
                );
                //                This.$sound_ok = $('<audio><source src="data:audio/wav;base64," /> </audio>');
                //            this.$sound_ok = $('<audio controls="controls" autobuffer="autobuffer" autoplay="autoplay"> <source src="data:audio/wav;base64," /> </audio>');
                $("body").append(this.$sound_ok);
                $("body").append(this.$sound_ko);
            }
            return this._super.apply(this, arguments);
        },
        _renderView: function () {
            return this._super(this, arguments).then(() => {
                _.defer(() => {
                    console.log("Form Renderer _renderView: ", this.$(".oe_kanban_action_button:visible"));
                });

            })
        },
        destroy: function() {
            this._super.apply(this, arguments);
            if (this._is_allowedModel()) {
                this.$sound_ok.remove();
                this.$sound_ko.remove();
            }
        },
        on_detach_callback: function() {
            this._super.apply(this, arguments);
            if (this._is_allowedModel()) {
                core.bus.off("keydown", this, this._onCoreKeyDown);
                core.bus.off("keyup", this, this._onCoreKeyUp);
                this.call(
                    "bus_service",
                    "off",
                    "notification",
                    this,
                    this.bus_notification
                );
            }
        },
        on_attach_callback: function() {
            this._super.apply(this, arguments);
            if (this._is_allowedModel()) {
                core.bus.on("keydown", this, this._onCoreKeyDown);
                core.bus.on("keyup", this, this._onCoreKeyUp);
                this.call(
                    "bus_service",
                    "on",
                    "notification",
                    this,
                    this.bus_notification
                );
                this.kanban_action_buttons = this.$(".oe_kanban_action_button:visible");
                console.log("Form Renderer: ", this.kanban_action_buttons);
                if (this.kanban_action_buttons.length){
                    this.kanban_action_buttons[0].focus();
                }
            }
        },
        bus_notification: function(notifications) {
            var self = this;
            _.each(notifications, function(notification) {
                var channel = notification[0];
                var message = notification[1];
                if (channel === "stock_barcodes_read-" + self.initialState.data.id) {
                    if (message.action === "focus") {
                        setTimeout(() => {
                            self.$(`[name=${message.field_name}] input`).select();
                        }, 300);
                        //                        Self.$(`[name=${message.field_name}] input`).select();
                    }
                } else if (
                    channel ===
                    "stock_barcodes_sound-" + self.initialState.data.id
                ) {
                    if (message.sound === "ok") {
                        self.$sound_ok[0].play();
                    } else if (message.sound === "ko") {
                        self.$sound_ko[0].play();
                    }
                }
            });
        },
        displayAccessKeys: function(){
            WebClientObj._addAccessKeyOverlays();
        },
        hideAccessKeys: function(){
            WebClientObj._hideAccessKeyOverlay();
        },
        pushKeyForChrome: function(keyDownEvent) {
            var prefixkey = "";
            if (keyDownEvent.shift){
                prefixkey += "shift+"
            }
            var elementWithAccessKey = document.querySelectorAll('[accesskey="' + prefixkey + keyDownEvent.key.toLowerCase() +
                '"], [accesskey="' + prefixkey + keyDownEvent.key.toUpperCase() + '"]');
            if (elementWithAccessKey.length) {
                keyDownEvent.preventDefault();
                elementWithAccessKey[0].focus();
                elementWithAccessKey[0].click();
            };
        },
        _onCoreKeyDown: function(ev) {
            if (this._is_allowedModel()) {
                const alt = ev.altKey || ev.key === "Alt",
                    newEvent = _.extend({}, ev),
                    shift = ev.shiftKey || ev.key === "Shift";
                // F2
                if (ev.keyCode === 113) {
                    if (WebClientObj._areAccessKeyVisible) {
                        this.hideAccessKeys();

                    } else {
                        WebClientObj._areAccessKeyVisible = true;
                        this.displayAccessKeys();
                    };
                } else if (WebClientObj._areAccessKeyVisible && !shift && !alt) {
                    if (this.BrowserDetection.isBrowserChrome()) {
                        this.pushKeyForChrome(ev);
                    } else {
                        newEvent.altKey = true;
                        newEvent.shiftKey = true;
                        WebClientObj._onKeyDown(newEvent);
                    };
                };

                // F9 key
                if (ev.keyCode === 120) {
                    this.$("button[name='action_clean_values']").click();
                }
                if (ev.keyCode === 123) {
                    this.$("button[name='open_actions']").click();
                }
                // Search kanban buttons to navigate
                if (ev.keyCode === $.ui.keyCode.UP || ev.keyCode === 117) {
                    console.log("UP: "+ev.keyCode);
                    ev.preventDefault();
                    --this.kanban_action_button_selected;
                    if (this.kanban_action_button_selected < 0) {
                        this.kanban_action_button_selected = this.kanban_action_buttons.length - 1;
                    }
                    this.kanban_action_buttons[this.kanban_action_button_selected].focus();
//                    ev.stopPropagation();
                };
                if (ev.keyCode === $.ui.keyCode.DOWN || ev.keyCode === 118) {
                    console.log("DOWN: "+ev.keyCode);
                    ev.preventDefault();
                    ++this.kanban_action_button_selected;
                    if (this.kanban_action_button_selected >= this.kanban_action_buttons.length) {
                        this.kanban_action_button_selected = 0;
                    }
                    this.kanban_action_buttons[this.kanban_action_button_selected].focus();
//                    ev.stopPropagation();
                };
                if (ev.keyCode === $.ui.keyCode.ENTER || ev.keyCode === 13) {
                    console.log(ev.keyCode);
                    this.kanban_action_buttons[this.kanban_action_button_selected].click();                };

            }
        },
        _onCoreKeyUp: function(ev) {
            if (this._is_allowedModel()) {
                // TODO: Remove
                //                console.log('KeyUp: ' + ev.keyCode);
                //                alert(ev.keyCode);
                // Intro key
                if (ev.keyCode === $.ui.keyCode.ENTER) {
                    this.$(":focus").click();
                    this.$("button[name='action_confirm']:visible").click();
                }
            }
        },
    });
});
